mapper.select:
SELECT u.*, t.taskTotal, t.taskFinishCount, t.taskTimelyCount, t.taskTimeoutCount, CONCAT(CAST(FORMAT((t.taskTimelyCount/t.taskFinishCount)*100,2) AS CHAR),"%") AS timelinessRate FROM `user` AS u LEFT JOIN ( SELECT uid, COUNT(uid) AS taskTotal, SUM(IF(`status` = "3", 1, 0)) AS taskFinishCount, SUM(IF(timely = "1", 1, 0)) AS taskTimelyCount, SUM(IF(timely = "2", 1, 0)) AS taskTimeoutCount FROM project_task
WHERE playStartTime >= #{startTime} AND playEndTime <= #{endTime}
GROUP BY uid HAVING uid IS NOT NULL ) AS t ON u.id = t.uid ${ew.customSqlSegment}
